data.command:
if (args[0] === "off" && args[2]['expiry-reason']?.includes("1Hit"))
{
console.log(args)
    const feature_name = "Lion's Roar - Marked"
    let damage = "1d6"

    const message_list = game.messages.filter(i => i.data.flags['midi-qol'])
    const last_message = message_list[message_list.length - 1]
    const workflow = MidiQOL.Workflow.getWorkflow(last_message.data.flags['midi-qol'].workflowId)
    const current_target = Array.from(workflow.targets)[0].actor
    const target_effects = Array.from(current_target.effects)

    if (target_effects.some((effect) => effect.data.label === feature_name))
    {
        if (workflow.isCritical === true) {damage = "2d6"}
        damage_roll = await new Roll(damage).evaluate({async : true})
        new MidiQOL.DamageOnlyWorkflow(workflow.actor, workflow.token, damage_roll.total, workflow.damageDetail[0].type, current_target ? [current_target] : [], damage_roll, {flavor: "Lion's Roar - Inspiration", itemCardId: workflow.itemCardId})

        let damage_total =  damage_roll.total + workflow.damageDetail[0].damage
        if (current_target.data.data.traits.dr.value.includes(workflow.damageDetail[0].type))
        {
            damage_total = Math.floor(damage_total/2)
        }

        const newHp = Math.max(current_target.data.data.attributes.hp.value - damage_total, 0)
        current_target.update({'data.attributes.hp.value' : newHp})
    }
    else
    {
        const item_owner = game.canvas.tokens.placeables.filter(i => i.actor.items.getName("Lion's Roar - Inspiration"))[0].actor
        let item = item_owner.items.getName("Lion's Roar - Inspiration")
        DAE.doEffects(item, true, [workflow.actor], {whisper: false})
    }
}
