onCombatEnd.script:
if (actor.system.resources.primary.value == 0 || actor.system.resources.primary.value == null ||
actor.system.resources.primary.value == undefined) { return; }

await actor.setFlag("world", "gw.resolveEOT", true);
await actor.update({
    "system.attributes.hp.value" : Math.max(0, actor.system.attributes.hp.value - actor.system.resources.primary.value), 
    "system.resources.primary.value" : 0,
});

onTurnEnd.script:
if (actor.system.resources.primary.value == 0 || actor.system.resources.primary.value == null ||
actor.system.resources.primary.value == undefined) { return; }

await actor.setFlag("world", "gw.resolveEOT", true);
await actor.update({
    "system.attributes.hp.value" : Math.max(0, actor.system.attributes.hp.value - actor.system.resources.primary.value), 
    "system.resources.primary.value" : 0,
});

macro.command:
if (game.combat === null) { return; }

if (actor.flags?.world?.gw?.resolveEOT === true) { 

    actor.unsetFlag("world", "gw.resolveEOT");
    return;
} 

if (args[0] === "onUpdateActor" && actor.flags?.world?.gw?.resolveEOT !== true) {
    const lastArg = args[args.length - 1];
    let newHP = getProperty(lastArg.updates, "system.attributes.hp.value");
    let oldHP = lastArg.targetActor.system.attributes.hp.value;
    if (newHP && oldHP && newHP < oldHP) {
        const damage = oldHP - newHP;
        const resolvePoolField = lastArg.sourceActor.system.resources.primary;
        if (!resolvePoolField) return;
        let resolvePoolMax = resolvePoolField.max;
        let resolvePool = resolvePoolField.value;
        if (resolvePoolMax === undefined) return;
        const absorbed = Math.min(damage, resolvePoolMax - resolvePool);

        if (absorbed) {
            newHP = newHP + absorbed;
            const speaker = ChatMessage.getSpeaker({ actor: lastArg.targetActor });
            ChatMessage.create({
                content: `${lastArg.originItem.name} absorbs ${absorbed} of ${damage} points of damage.<br> Hp -> ${newHP}<br>Resolve -> ${resolvePool + absorbed}/${resolvePoolMax}`,
                speaker
            });
            lastArg.updates.system.attributes.hp.value = newHP;
            actor.update({"system.resources.primary.value": resolvePool + absorbed})
            actor.setFlag("world", "gw.makeAegis", true);
        }
    }
    return true;
}
