onCombatStart.script:
if ( actor.getFlag("world", "gw.stamina") > origin.system.uses.value || origin.system.uses.value === 0 ) {

    await origin.update({
        "system.uses.value": Math.min(origin.system.uses.value + 1, origin.system.uses.max),
    });
}

actor.setFlag("world", "gw.stamina", origin.system.uses.value);
