// Set the feature's "Activation Condition" as NFM, and set it to X/X charges.
main();

async function refresh(item) {
    let tag = "NFM"; //non-flourish-manuever
    if (item.system.activation) {
        if (item.system.activation.condition === tag) {
            await item.update({ "system.uses.value": item.system.uses.max });
        }
    }
}

async function main() {
    let actors = canvas.tokens.placeables.filter(t => Object.keys(t.actor.ownership).includes(game.userId)).map(t => t.actor);

    actors.forEach(act => act.items.forEach(refresh));
}
