async function becomeFire(token, workflow) {

    async function effectMacro() {
        await warpgate.revert(token.document, 'Become Fire');
    }

    let targetToken = workflow.token;
    let effectData = {
        'label': "Become Fire",
        'icon': workflow.item.img,
        'duration': {
            "rounds": 1,
        },
        'origin': workflow.item.uuid,
        'flags': {
            'effectmacro': {
                'dae': {
                    'specialDuration': ['turnstartSource']
                },
                'onDelete': {
                    'script': `(${effectMacro})()`
                }
            },
        }
    };
    let updates = {
        'embedded': {
            'Item': {
                [featureData.name]: featureData
            },
            'ActiveEffect': {
                [featureData.name]: effectData
            }
        }
    };
    let options = {
        'permanent': false,
        'name': "Become Fire",
        'description': "Become Fire"
    };
    await warpgate.mutate(targetToken.document, updates, {}, options);
}

await becomeFire(token, workflow)
