const targets = args[0].targets;

let workflow = MidiQOL.Workflow.getWorkflow(args[0].uuid);

const effects = workflow.actor.effects.filter(effect => effect.label.includes("Marked"));
const markers = [];

for (const effect of effects) {

    if (effect.flags?.isConvenient === true) {

        const tokenId = effect.origin.split(".")[3];

        markers.push(canvas.tokens.placeables.filter(tok => tok.id === tokenId).map(tok => tok.actor.id)[0]);
    }
    else {
        markers.push(effect.origin.split(".")[1]);
    }



}

console.log(markers);

const non_markers = targets.filter(target => !markers.includes(target.actor.id));

let marked = true;
for (var marker of markers) {
    for (var target of targets) {
        if (target.actor.id === marker) {
            marked = false;
            break;
        }
    }
}

if (marked) {
    workflow.disadvantage = true;
    if (workflow.templateData != null || workflow.templateData != undefined) {
        await game.macros.getName("save_buff").execute(...args);
    }
}


console.log("Marked Executed Successfully");
