const feat_name = "Inspiration Shards";


Hooks.on("createChatMessage", main);

function main(args) {

    if (game.combat != null) { return; }

    let data = args;

    if (data.flags?.dnd5e?.roll?.type == null || data.flags?.dnd5e?.roll?.type == undefined) { return; }

    if (data.flags?.dnd5e?.roll?.skillId == null || data.flags?.dnd5e?.roll?.skillId == undefined) { return; }


    new Dialog({
        title: "Inspiration",
        content: "Looks like you made a skill check ;) \n . What was the outcome?",
        buttons: {
            SUCCESS: { label: "Success", callback: () => { return Success(data); } },
            FAILURE: { label: "Failure", callback: () => { return; } },
        }
    }).render(true);

}

async function Success(data) {

    const actor = await fromUuid(`Actor.${data.speaker.actor}`);
    let item = actor.items.filter(it => it.name === feat_name)[0];

    const uses = item.system.uses.value;

    if (uses === (item.system.uses.max - 1)) {

        await  MidiQOL.socket().executeAsGM("createEffects", { actorUuid: actor.uuid, effects: [[...item.effects][0]] });


        await item.update({"system.uses.value": 0});
        return;
    }

    await item.update({"system.uses.value": uses+1});
    return;
}
