async function becomeWater(token, workflow) {

    let concentrationEffect = effectUtils.getConcentrationEffect(workflow.actor, workflow.item);
    let targetToken = workflow.token;
    let effectData = {
        'label': "Become Water",
        'icon': workflow.item.img,
        'disabled': false,
        'duration': {
            "rounds": 1,
        },
        'origin': workflow.item.uuid,
        'flags': {
            'dae': {
                'specialDuration': ['turnstartSource']
            },
        }
    };

    let effect = await effectUtils.createEffect(workflow.actor, effectData, {concentrationItem: workflow.item, strictlyInterdependent: true, 
        identifier: "Become Water".split(' ').map((w, i, _) => i == 0 ? w.toLowerCase() : `${w[0].toUpperCase()}${w.toLowerCase().slice(1)}`).reduce((res, w) => res + w)});
    await itemUtils.createItems(workflow.actor, [featureData], {favorite: true, parentEntity: effect});
    if (concentrationEffect) await genericUtils.update(concentrationEffect, {'duration.seconds': effectData.duration.seconds});
}

